import React, { useState } from 'react';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [batch, setBatch] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate data
    if (!name || !age || !batch) {
      alert("Please fill in all fields!");
      return;
    }

    setIsLoading(true);

    // Prepare request data
    const data = {
      name,
      age,
      batch,
    };

    try {
      // Send API call using a separate function
      const response = await enrollYogaStudent(data);

      setIsLoading(false);

      if (response.success) {
        alert("Successfully enrolled! Please proceed to payment.");
      } else {
        alert("Error enrolling: " + response.message);
      }
    } catch (error) {
      setIsLoading(false);
      console.error(error);
      alert("An unexpected error occurred!");
    }
  };

  const enrollYogaStudent = async (data) => {
    // Replace the URL with your backend API endpoint
    const response = await fetch("http://localhost:5000/api/enroll", {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
    });

    return await response.json();
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              disabled={isLoading}
            />
          </label>
          <label>
            Age:
            <input
              type="number"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              disabled={isLoading}
            />
          </label>
          <label>
            Batch:
            <select value={batch} onChange={(e) => setBatch(e.target.value)}>
              <option value="">Select Batch</option>
              <option value="6-7AM">6-7 AM</option>
              <option value="7-8AM">7-8 AM</option>
              <option value="8-9AM">8-9 AM</option>
              <option value="5-6PM">5-6 PM</option>
            </select>
          </label>
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Loading..." : "Enroll"}
          </button>
        </form>
      </header>
    </div>
  );
}

export default App;
