import React, { useState } from 'react';
import './App.css';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [batch, setBatch] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!name || !age || !batch) {
      setError("Please fill in all fields!");
      return;
    }

    setIsLoading(true);

    const data = {
      name,
      age,
      batch,
    };

    try {
      const response = await enrollYogaStudent(data);

      setIsLoading(false);

      if (response.success) {
        alert("Successfully enrolled! Please proceed to payment.");
      } else {
        setError(response.message);
      }
    } catch (error) {
      setIsLoading(false);
      console.error(error);
      setError("An unexpected error occurred!");
    }
  };

  const enrollYogaStudent = async (data) => {
    const response = await fetch("https://yoga-application-alpha.vercel.c/api/enroll", {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
    });

    return await response.json();
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Yoga Studio</h1>
        <form onSubmit={handleSubmit}>
          <h2>Yoga Admission Form</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              disabled={isLoading}
            />
          </div>
          <div className="form-group">
            <label>Age:</label>
            <input
              type="number"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              disabled={isLoading}
            />
          </div>
          <div className="form-group">
            <label>Batch:</label>
            <select value={batch} onChange={(e) => setBatch(e.target.value)}>
              <option value="">Select Batch</option>
              <option value="6-7AM">6-7 AM</option>
              <option value="7-8AM">7-8 AM</option>
              <option value="8-9AM">8-9 AM</option>
              <option value="5-6PM">5-6 PM</option>
            </select>
          </div>
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Loading..." : "Enroll"}
          </button>
        </form>
      </header>
    </div>
  );
}

export default App;
